import { Component } from '@angular/core';

interface Food {
  name: string;
  calories: number;
  image: string;
  quantity?: number; // Optional property for quantity
}

@Component({
  selector: 'app-food-list',
  templateUrl: './app-food-list.component.html',
  styleUrls: ['./app-food-list.component.css']
})
export class AppFoodListComponent {
  foods: Food[] = []; // Initialize with your food data
  newFood: Food = { name: '', calories: 0, image: '' };
  todaysFoods: Food[] = [];
  searchText: string = '';
  showAddForm = false;

  addFood() {
    this.foods.push(this.newFood);
    this.newFood = { name: '', calories: 0, image: '' };
    this.showAddForm = false;
  }

  addToTodaysFoods(food: Food, quantity: number = 1) {
    for (let i = 0; i < quantity; i++) {
      this.todaysFoods.push(food);
    }
  }
}
<div>
  <input type="text" [(ngModel)]="searchText" placeholder="Search by name" />
</div>
<div *ngFor="let food of foods | foodFilter: searchText">
  <img [src]="food.image" alt="Food Image" />
  <p>Name: {{ food.name }}</p>
  <p>Calories: {{ food.calories }}</p>
  <input type="number" [(ngModel)]="food.quantity" />
  <button (click)="addToTodaysFoods(food, food.quantity)">Add to Today's List</button>
</div>

<button (click)="showAddForm = !showAddForm">Add New Food</button>
<div *ngIf="showAddForm">
  <form>
    <input type="text" [(ngModel)]="newFood.name" placeholder="Food Name" />
    <input type="number" [(ngModel)]="newFood.calories" placeholder="Calories" />
    <input type="text" [(ngModel)]="newFood.image" placeholder="Image URL" />
    <button type="button" (click)="addFood()">Submit</button>
  </form>
</div>
